                                    <div className="serv-sort-content">
                                        <a href="/">Sort 1</a>
                                        <div className="serv-drop-divider"/>
                                        <a href="/">Sort 2</a>
                                        <div className="serv-drop-divider"/>
                                        <a href="/">Sort 3</a>
                                    </div>
                                </div>
                            </div>

                        </header>

                        {/* Service Box Container */}
                        <div className="services-grid-layout">
                            {services}
                        </div>
                    </div>

                </div>


            </div>
        );
    }
}

export default ServicesScreen;
